package main

import (
	"bufio"
	"fmt"
	"os"
	"path/filepath"
	"sort"
	"strconv"
	"strings"
	"time"

	"github.com/charmbracelet/bubbles/viewport"
	tea "github.com/charmbracelet/bubbletea"
	"github.com/charmbracelet/lipgloss"
)

var (
	titleStyle = lipgloss.NewStyle().
			Bold(true).
			Foreground(lipgloss.Color("#FAFAFA")).
			Background(lipgloss.Color("#7D56F4")).
			Padding(0, 1)

	barStyle = lipgloss.NewStyle().
			Background(lipgloss.Color("#34D399")).
			MarginRight(2)

	axisStyle = lipgloss.NewStyle().
			Foreground(lipgloss.Color("#A1A1AA"))

	helpStyle = lipgloss.NewStyle().Foreground(lipgloss.Color("#71717A"))
)

type model struct {
	data     map[string]int
	days     []string
	viewport viewport.Model
	ready    bool
}

func main() {
	p := tea.NewProgram(
		initialModel(),
		tea.WithAltScreen(),
	)
	if _, err := p.Run(); err != nil {
		fmt.Printf("Error: %v", err)
		os.Exit(1)
	}
}

func initialModel() model {
	return model{
		data:     loadData(),
		viewport: viewport.Model{},
	}
}

func loadData() map[string]int {
	data := make(map[string]int)
	home, _ := os.UserHomeDir()
	path := filepath.Join(home, ".screentime.csv")

	file, err := os.Open(path)
	if err != nil {
		return data
	}
	defer file.Close()

	scanner := bufio.NewScanner(file)
	for scanner.Scan() {
		parts := strings.Split(scanner.Text(), ",")
		if len(parts) != 2 {
			continue
		}
		minutes, _ := strconv.Atoi(parts[1])
		data[parts[0]] = minutes / 60 // Convert seconds to hours
	}
	return data
}

func (m model) Init() tea.Cmd {
	return nil
}

func (m model) Update(msg tea.Msg) (tea.Model, tea.Cmd) {
	switch msg := msg.(type) {
	case tea.WindowSizeMsg:
		if !m.ready {
			m.viewport = viewport.Model{Width: msg.Width, Height: msg.Height - 2}
			m.ready = true
		} else {
			m.viewport.Width = msg.Width
			m.viewport.Height = msg.Height - 2
		}
	case tea.KeyMsg:
		if msg.Type == tea.KeyCtrlC || msg.String() == "q" {
			return m, tea.Quit
		}
	}
	return m, nil
}

func (m model) View() string {
	if !m.ready {
		return "Initializing..."
	}

	var days []string
	for i := 0; i < 7; i++ {
		date := time.Now().AddDate(0, 0, -i).Format("2006-01-02")
		days = append(days, date)
	}
	sort.Strings(days)

	var maxHours int
	for _, day := range days {
		if m.data[day] > maxHours {
			maxHours = m.data[day]
		}
	}

	graphHeight := m.viewport.Height - 4
	if graphHeight < 5 {
		graphHeight = 5
	}

	var bars []string
	for _, day := range days {
		hours := m.data[day]
		barHeight := int(float64(hours) / float64(maxHours) * float64(graphHeight))
		if maxHours == 0 {
			barHeight = 0
		}

		var bar strings.Builder
		for i := 0; i < barHeight; i++ {
			bar.WriteString(barStyle.Render(" "))
			bar.WriteString("\n")
		}

		label := time.Now().Format("Mon")
		if day != time.Now().Format("2006-01-02") {
			t, _ := time.Parse("2006-01-02", day)
			label = t.Format("Mon")
		}

		bars = append(bars, lipgloss.JoinVertical(
			lipgloss.Center,
			axisStyle.Render(fmt.Sprintf("%2dh", hours)),
			bar.String(),
			axisStyle.Render(label),
		))
	}

	chart := lipgloss.JoinHorizontal(
		lipgloss.Top,
		bars...,
	)

	return lipgloss.JoinVertical(
		lipgloss.Left,
		titleStyle.Render("Screen Time Tracker"),
		chart,
		helpStyle.Render("q: quit • ←/→: navigate"),
	)
}

