package main

import (
	"fmt"
	"os"
	"path/filepath"
	"strings"
	"time"

	tea "github.com/charmbracelet/bubbletea"
	"github.com/charmbracelet/lipgloss"
)

const (
	daysToShow = 7
	barWidth   = 10
	maxHours   = 24
)

var (
	titleStyle    = lipgloss.NewStyle().Bold(true).Foreground(lipgloss.Color("5"))
	barStyle      = lipgloss.NewStyle().Foreground(lipgloss.Color("2"))
	axisStyle     = lipgloss.NewStyle().Foreground(lipgloss.Color("8"))
	labelStyle    = lipgloss.NewStyle().Foreground(lipgloss.Color("7"))
	statStyle     = lipgloss.NewStyle().Foreground(lipgloss.Color("6"))
	currentStyle  = lipgloss.NewStyle().Foreground(lipgloss.Color("4"))
)

type model struct {
	screenTimes map[string]int
	days        []string
}

func main() {
	p := tea.NewProgram(initialModel(), tea.WithAltScreen())
	if _, err := p.Run(); err != nil {
		fmt.Printf("Error: %v", err)
		os.Exit(1)
	}
}

func initialModel() model {
	data := loadData()
	return model{
		screenTimes: data,
		days:        getLastNDays(daysToShow),
	}
}

func (m model) Init() tea.Cmd { return nil }

func (m model) Update(msg tea.Msg) (tea.Model, tea.Cmd) {
	switch msg := msg.(type) {
	case tea.KeyMsg:
		switch msg.String() {
		case "q", "ctrl+c":
			return m, tea.Quit
		}
	}
	return m, nil
}

func (m model) View() string {
	var maxSeconds int
	for _, v := range m.screenTimes {
		if v > maxSeconds {
			maxSeconds = v
		}
	}

	sb := strings.Builder{}
	sb.WriteString(titleStyle.Render("Screen Time Tracker\n\n"))

	// Chart
	for i := maxHours; i >= 0; i-- {
		line := strings.Builder{}
		for _, day := range m.days {
			seconds := m.screenTimes[day]
			hours := float64(seconds) / 3600
			height := int((hours / float64(maxHours)) * float64(maxHours))

			if height >= i {
				line.WriteString(barStyle.Render(strings.Repeat("█", barWidth)))
			} else {
				line.WriteString(strings.Repeat(" ", barWidth))
			}
		}
		sb.WriteString(line.String() + "\n")
	}

	// X-axis
	sb.WriteString(axisStyle.Render(strings.Repeat("─", barWidth*daysToShow)) + "\n")

	// Day labels
	labels := strings.Builder{}
	for _, day := range m.days {
		label := day[5:] // Show only day
		if day == time.Now().Format("2006-01-02") {
			labels.WriteString(currentStyle.Render(fmt.Sprintf("%-*s", barWidth, label)))
		} else {
			labels.WriteString(labelStyle.Render(fmt.Sprintf("%-*s", barWidth, label)))
		}
	}
	sb.WriteString(labels.String() + "\n\n")

	// Stats
	today := time.Now().Format("2006-01-02")
	todaySeconds := m.screenTimes[today]
	sb.WriteString(statStyle.Render(fmt.Sprintf(" Today: %s\n Total: %s",
		formatDuration(todaySeconds),
		formatDuration(totalTime(m.screenTimes)),
	))

	return sb.String()
}

func loadData() map[string]int {
	data := make(map[string]int)
	home, _ := os.UserHomeDir()
	file, err := os.ReadFile(filepath.Join(home, ".screentime/screentime.log"))
	if err != nil {
		return data
	}

	for _, line := range strings.Split(string(file), "\n") {
		parts := strings.Fields(line)
		if len(parts) == 2 {
			date := parts[0]
			seconds := 0
			fmt.Sscanf(parts[1], "%d", &seconds)
			data[date] = seconds
		}
	}
	return data
}

func getLastNDays(n int) []string {
	days := make([]string, n)
	now := time.Now()
	for i := range days {
		days[i] = now.AddDate(0, 0, -i).Format("2006-01-02")
	}
	// Reverse to show oldest first
	for i := 0; i < len(days)/2; i++ {
		j := len(days) - 1 - i
		days[i], days[j] = days[j], days[i]
	}
	return days
}

func formatDuration(seconds int) string {
	hours := seconds / 3600
	minutes := (seconds % 3600) / 60
	return fmt.Sprintf("%02d:%02d", hours, minutes)
}

func totalTime(data map[string]int) int {
	total := 0
	for _, v := range data {
		total += v
	}
	return total
}

