package main

import (
	"fmt"
	"os"
	"os/exec"
	"strings"
	"time"

	"github.com/charmbracelet/bubbles/list"
	"github.com/charmbracelet/bubbles/textinput"
	"github.com/charmbracelet/bubbles/viewport"
	tea "github.com/charmbracelet/bubbletea"
	"github.com/charmbracelet/lipgloss"
)

// Model Definitions
type model struct {
	wifiList        list.Model
	connectedList   list.Model
	details         viewport.Model
	activeTab       int
	tabs            []string
	ethConnected    bool
	passwordInput   textinput.Model
	showingPassword bool
	statusMessage   string
}

type wifiNetwork struct {
	ssid      string
	strength  string
	security  string
	connected bool
}

// Command Execution Helpers
func runCmd(cmd string, args ...string) (string, error) {
	out, err := exec.Command(cmd, args...).CombinedOutput()
	return string(out), err
}

func getNetworks() []list.Item {
	out, _ := runCmd("nmcli", "-t", "-f", "SSID,SIGNAL,SECURITY", "device", "wifi", "list")
	networks := []list.Item{}

	for _, line := range strings.Split(out, "\n") {
		parts := strings.Split(line, ":")
		if len(parts) < 3 {
			continue
		}
		networks = append(networks, wifiNetwork{
			ssid:     parts[0],
			strength: parts[1],
			security: parts[2],
		})
	}
	return networks
}

// UI Configuration
var (
	tabStyle       = lipgloss.NewStyle().Padding(0, 2)
	activeTabStyle = tabStyle.Copy().Foreground(lipgloss.Color("205"))
	ethStyle       = lipgloss.NewStyle().Foreground(lipgloss.Color("39")).Bold(true)
)

// Main Model Setup
func initialModel() model {
	wifi := list.New(getNetworks(), list.NewDefaultDelegate(), 40, 20)
	wifi.Title = "Available Networks"

	connected := list.New([]list.Item{}, list.NewDefaultDelegate(), 40, 5)
	connected.Title = "Connected Networks"

	details := viewport.New(40, 10)

	ti := textinput.New()
	ti.Placeholder = "Password"
	ti.EchoMode = textinput.EchoPassword

	return model{
		wifiList:      wifi,
		connectedList: connected,
		details:       details,
		activeTab:     0,
		tabs:          []string{"Wi-Fi", "Connected", "Details"},
		passwordInput: ti,
	}
}

// Update Loop
func (m model) Init() tea.Cmd {
	return tea.Batch(
		tea.EnterAltScreen,
		tick(),
	)
}

func tick() tea.Cmd {
	return tea.Tick(time.Second*5, func(time.Time) tea.Msg {
		return refreshMsg{}
	})
}

func (m model) Update(msg tea.Msg) (tea.Model, tea.Cmd) {
	switch msg := msg.(type) {
	case tea.KeyMsg:
		switch msg.String() {
		case "ctrl+c", "q":
			return m, tea.Quit
		case "right":
			m.activeTab = (m.activeTab + 1) % len(m.tabs)
		case "left":
			m.activeTab = (m.activeTab - 1 + len(m.tabs)) % len(m.tabs)
		}
	case refreshMsg:
		m.wifiList.SetItems(getNetworks())
		return m, tick()
	}

	// Handle tab-specific updates
	var cmd tea.Cmd
	switch m.activeTab {
	case 0:
		m.wifiList, cmd = m.wifiList.Update(msg)
	case 1:
		m.connectedList, cmd = m.connectedList.Update(msg)
	case 2:
		m.details, cmd = m.details.Update(msg)
	}

	return m, cmd
}

// View Renderer
func (m model) View() string {
	// Ethernet status
	ethStatus := ""
	if m.ethConnected {
		ethStatus = ethStyle.Render("ó°ˆ€ ")
	}

	// Tabs
	tabs := []string{}
	for i, t := range m.tabs {
		if i == m.activeTab {
			tabs = append(tabs, activeTabStyle.Render(t))
		} else {
			tabs = append(tabs, tabStyle.Render(t))
		}
	}
	tabRow := lipgloss.JoinHorizontal(lipgloss.Top, tabs...)

	// Main content
	var content string
	switch m.activeTab {
	case 0:
		content = m.wifiList.View()
	case 1:
		content = m.connectedList.View()
	case 2:
		content = m.details.View()
	}

	return fmt.Sprintf("%s\n%s\n%s", ethStatus, tabRow, content)
}

// Main Function
func main() {
	if os.Geteuid() == 0 {
		fmt.Println("Don't run as root!")
		os.Exit(1)
	}

	p := tea.NewProgram(initialModel(), tea.WithAltScreen())
	if err := p.Start(); err != nil {
		fmt.Printf("Error: %v", err)
		os.Exit(1)
	}
}

