)

var (
	appStyle       = lipgloss.NewStyle().Padding(1, 2)
	titleStyle     = lipgloss.NewStyle().Bold(true).Foreground(lipgloss.Color("63"))
	statusStyle    = lipgloss.NewStyle().Foreground(lipgloss.Color("241")).PaddingLeft(1)
	connectedStyle = lipgloss.NewStyle().Foreground(lipgloss.Color("46"))
	errorStyle     = lipgloss.NewStyle().Foreground(lipgloss.Color("196"))
	ethStyle       = lipgloss.NewStyle().Foreground(lipgloss.Color("39")).Bold(true)
)

type wifiNetwork struct {
	SSID      string
	Strength  int
	Secure    bool
	Connected bool
}

func (w wifiNetwork) Title() string       { return w.SSID }
func (w wifiNetwork) Description() string { 
	status := ""
	if w.Connected {
		status = connectedStyle.Render("âœ“ Connected")
	}
	return fmt.Sprintf("Strength: %d%% %s", w.Strength, status)
}
func (w wifiNetwork) FilterValue() string { return w.SSID }

type model struct {
	list         list.Model
	ethConnected bool
	password     textinput.Model
	showPassword bool
	selected     *wifiNetwork
	status       string
	qrCode       string
}

type refreshMsg []wifiNetwork
type ethStatusMsg bool

func main() {
	initialModel := model{
		list:         list.New([]list.Item{}, list.NewDefaultDelegate(), 0, 0),
		password:     textinput.New(),
		ethConnected: false,
	}

	initialModel.list.Title = "Available Wi-Fi Networks"
	initialModel.password.Placeholder = "Password"
	initialModel.password.EchoMode = textinput.EchoPassword

	p := tea.NewProgram(initialModel, tea.WithAltScreen())
	if _, err := p.Run(); err != nil {
		fmt.Printf("Error: %v", err)
		os.Exit(1)
	}
}

func (m model) Init() tea.Cmd {
	return tea.Batch(
		refreshNetworks(),
		checkEthernetStatus(),
	)
}

func (m model) Update(msg tea.Msg) (tea.Model, tea.Cmd) {
	switch msg := msg.(type) {
	case tea.KeyMsg:
		if m.showPassword {
			switch {
			case key.Matches(msg, keys.Enter):
				return m.connectToNetwork()
			case key.Matches(msg, keys.Escape):
				m.showPassword = false
				return m, nil
			}
		}

		switch {
		case key.Matches(msg, keys.Quit):
			return m, tea.Quit
		case key.Matches(msg, keys.Refresh):
			return m, refreshNetworks()
		case key.Matches(msg, keys.Connect):
			if item, ok := m.list.SelectedItem().(wifiNetwork); ok {
				m.selected = &item
				if item.Secure && !item.Connected {
					m.showPassword = true
					m.password.Focus()
					return m, nil
				}
				return m.connectToNetwork()
			}
		}

	case tea.WindowSizeMsg:
		h, v := appStyle.GetFrameSize()
		m.list.SetSize(msg.Width-h, msg.Height-v)

	case refreshMsg:
		m.list.SetItems(msg)
		return m, tea.Tick(5*time.Second, func(t time.Time) tea.Msg {
			return refreshNetworks()()
		})

	case ethStatusMsg:
		m.ethConnected = bool(msg)
		return m, tea.Tick(10*time.Second, func(t time.Time) tea.Msg {
			return checkEthernetStatus()()
		})
	}

	var cmd tea.Cmd
	if m.showPassword {
		m.password, cmd = m.password.Update(msg)
	} else {
		m.list, cmd = m.list.Update(msg)
	}
	return m, cmd
}

func (m model) View() string {
	eth := ""
	if m.ethConnected {
		eth = ethStyle.Render("ó°ˆ€ Ethernet")
	}

	view := strings.Join([]string{
		titleStyle.Render("Network Manager"),
		m.list.View(),
		eth,
		"\n" + m.status,
	}, "\n")

	if m.showPassword {
		view = fmt.Sprintf(
			"%s\nEnter password for %s:\n%s",
			view,
			m.selected.SSID,
			m.password.View(),
		)
	}

	if m.qrCode != "" {
		view = fmt.Sprintf("%s\n%s", view, m.qrCode)
	}

	return appStyle.Render(view)
}

func refreshNetworks() tea.Msg {
	cmd := exec.Command("nmcli", "-t", "-f", "SSID,SIGNAL,SECURITY,IN-USE", "device", "wifi", "list")
	out, err := cmd.Output()
	if err != nil {
		return refreshMsg{}
	}

	networks := []wifiNetwork{}
	for _, line := range strings.Split(string(out), "\n") {
		fields := strings.Split(line, ":")
		if len(fields) < 4 {
			continue
		}

		strength := 0
		fmt.Sscanf(fields[1], "%d", &strength)
		connected := strings.Trim(fields[3], " ") == "*"

		networks = append(networks, wifiNetwork{
			SSID:      fields[0],
			Strength:  strength,
			Secure:    fields[2] != "",
			Connected: connected,
		})
	}
	return refreshMsg(networks)
}

func checkEthernetStatus() tea.Msg {
	cmd := exec.Command("nmcli", "-t", "-f", "TYPE,STATE", "device", "status")
	out, _ := cmd.Output()
	for _, line := range strings.Split(string(out), "\n") {
		fields := strings.Split(line, ":")
		if len(fields) >= 2 && fields[0] == "ethernet" {
			return ethStatusMsg(strings.Trim(fields[1], " ") == "connected")
		}
	}
	return ethStatusMsg(false)
}

func (m *model) connectToNetwork() (tea.Model, tea.Cmd) {
	m.showPassword = false
	args := []string{"device", "wifi", "connect", m.selected.SSID}
	if m.selected.Secure {
		args = append(args, "password", m.password.Value)
	}

	ctx, cancel := context.WithTimeout(context.Background(), 10*time.Second)
	defer cancel()

	cmd := exec.CommandContext(ctx, "nmcli", args...)
	out, err := cmd.CombinedOutput()
	if err != nil {
		m.status = errorStyle.Render(fmt.Sprintf("Error: %s", out))
	} else {
		m.status = connectedStyle.Render("Successfully connected!")
		m.qrCode = generateQRCode(m.selected.SSID, m.password.Value)
	}

	m.password.Reset()
	return m, refreshNetworks()
}

func generateQRCode(ssid, password string) string {
	if password == "" {
		return ""
	}
	qr, _ := qrcode.New(fmt.Sprintf("WIFI:S:%s;T:WPA;P:%s;;", ssid, password), qrcode.Medium)
	return qr.ToSmallString(false)
}

var keys = keyMap{
	Quit:    key.NewBinding(key.WithKeys("q", "ctrl+c")),
	Refresh: key.NewBinding(key.WithKeys("r")),
	Connect: key.NewBinding(key.WithKeys("enter")),
	Escape:  key.NewBinding(key.WithKeys("esc")),
}

type keyMap struct {
	Quit    key.Binding
	Refresh key.Binding
	Connect key.Binding
	Escape  key.Binding
}

func (k keyMap) ShortHelp() []key.Binding {
	return []key.Binding{k.Quit, k.Refresh, k.Connect}
}

