package main

import (
	"fmt"
	"os"
	"os/exec"
	"regexp"
	"strings"
	"time"

	tea "github.com/charmbracelet/bubbletea"
	"github.com/charmbracelet/lipgloss"
	qrcode "github.com/skip2/go-qrcode"
)

// Styles
var (
	titleStyle    = lipgloss.NewStyle().Bold(true).Foreground(lipgloss.Color("5"))
	listStyle     = lipgloss.NewStyle().MarginRight(2)
	detailStyle   = lipgloss.NewStyle().Padding(1, 2).Border(lipgloss.RoundedBorder())
	statusStyle   = lipgloss.NewStyle().Foreground(lipgloss.Color("6")).MarginTop(1)
	errorStyle    = lipgloss.NewStyle().Foreground(lipgloss.Color("1"))
	selectedStyle = lipgloss.NewStyle().Foreground(lipgloss.Color("2"))
)

type network struct {
	ssid      string
	strength  string
	security  string
	connected bool
}

type model struct {
	networks       []network
	selected       int
	ethernetActive bool
	lastUpdate     time.Time
	errorMsg       string
	qrContent      string
	passwordInput  string
	state          string
}

func (m model) Init() tea.Cmd {
	return tea.Batch(
		tickCmd(),
		refreshNetworksCmd(),
		checkEthernetCmd(),
	)
}

func (m *model) Update(msg tea.Msg) (tea.Model, tea.Cmd) {
	switch msg := msg.(type) {
	case tea.KeyMsg:
		return m.handleKeys(msg)
	case []network:
		m.networks = msg
		m.lastUpdate = time.Now()
	case bool:
		m.ethernetActive = msg
	case string:
		if msg == "refresh" {
			return m, refreshNetworksCmd()
		}
	case error:
		m.errorMsg = msg.Error()
		return m, nil
	}
	return m, nil
}

func (m *model) handleKeys(msg tea.KeyMsg) (tea.Model, tea.Cmd) {
	switch msg.String() {
	case "q", "ctrl+c":
		return m, tea.Quit
	case "up", "k":
		if m.selected > 0 {
			m.selected--
		}
	case "down", "j":
		if m.selected < len(m.networks)-1 {
			m.selected++
		}
	case "enter":
		return m.handleNetworkAction()
	case "r":
		return m, refreshNetworksCmd()
	case "esc":
		m.state = "list"
		m.passwordInput = ""
	}
	return m, nil
}

func (m *model) View() string {
	if m.state == "qr" {
		return detailStyle.Render(m.generateQR())
	}

	var b strings.Builder
	b.WriteString(titleStyle.Render("Available Wi-Fi Networks") + "\n")

	for i, n := range m.networks {
		line := fmt.Sprintf("%s %s (%s)", n.ssid, n.strength, n.security)
		if i == m.selected {
			line = selectedStyle.Render("> " + line)
		}
		if n.connected {
			line += " (Connected)"
		}
		b.WriteString(line + "\n")
	}

	status := fmt.Sprintf("Ethernet: %t | Last updated: %s",
		m.ethernetActive, m.lastUpdate.Format("15:04:05"))

	if m.errorMsg != "" {
		status += errorStyle.Render("\nError: " + m.errorMsg)
	}

	return lipgloss.JoinVertical(lipgloss.Left,
		listStyle.Render(b.String()),
		statusStyle.Render(status),
	)
}

// NetworkManager interaction functions
func refreshNetworksCmd() tea.Cmd {
	return tea.Exec(exec.Command("nmcli", "-t", "dev", "wifi", "list"), func(err error) tea.Msg {
		if err != nil {
			return err
		}
		out, _ := exec.Command("nmcli", "-t", "dev", "wifi", "list").Output()
		return parseNetworks(string(out))
	})
}

func parseNetworks(output string) []network {
	var networks []network
	lines := strings.Split(output, "\n")
	re := regexp.MustCompile(`(.+):(.+):(.+):(.+):(.+):(.+):`)

	for _, line := range lines {
		matches := re.FindStringSubmatch(line)
		if len(matches) > 6 {
			networks = append(networks, network{
				ssid:     matches[1],
				strength: matches[4],
				security: matches[5],
			})
		}
	}
	return networks
}

func checkEthernetCmd() tea.Cmd {
	return tea.Exec(exec.Command("nmcli", "-t", "con", "show", "--active"), func(err error) tea.Msg {
		out, _ := exec.Command("nmcli", "-t", "con", "show", "--active").Output()
		return strings.Contains(string(out), "ethernet")
	})
}

// QR Code generation
func (m *model) generateQR() string {
	if m.qrContent == "" {
		return "No QR content available"
	}
	qr, _ := qrcode.New(m.qrContent, qrcode.Medium)
	return qr.ToSmallString(false)
}

func main() {
	p := tea.NewProgram(&model{
		state: "list",
	}, tea.WithAltScreen())
	if err := p.Start(); err != nil {
		fmt.Println("Error running program:", err)
		os.Exit(1)
	}
}

