        return refreshMsg{}
    })
}

func (m model) Update(msg tea.Msg) (tea.Model, tea.Cmd) {
    switch msg := msg.(type) {
    case tea.KeyMsg:
        switch msg.String() {
        case "ctrl+c", "q":
            return m, tea.Quit
        case "right":
            m.activeTab = (m.activeTab + 1) % len(m.tabs)
        case "left":
            m.activeTab = (m.activeTab - 1 + len(m.tabs)) % len(m.tabs)
        }
    case refreshMsg:
        m.wifiList.SetItems(getNetworks())
        return m, tick()
    }

    // Handle tab-specific updates
    var cmd tea.Cmd
    switch m.activeTab {
    case 0:
        m.wifiList, cmd = m.wifiList.Update(msg)
    case 1:
        m.connectedList, cmd = m.connectedList.Update(msg)
    case 2:
        m.details, cmd = m.details.Update(msg)
    }

    return m, cmd
