    for i, t := range m.tabs {
        if i == m.activeTab {
            tabs = append(tabs, activeTabStyle.Render(t))
        } else {
            tabs = append(tabs, tabStyle.Render(t))
        }
    }
    tabRow := lipgloss.JoinHorizontal(lipgloss.Top, tabs...)

    // Main content
    var content string
    switch m.activeTab {
    case 0:
        content = m.wifiList.View()
    case 1:
        content = m.connectedList.View()
    case 2:
        content = m.details.View()
    }

    return fmt.Sprintf("%s\n%s\n%s", ethStatus, tabRow, content)
}

// Main Function
