	tea "github.com/charmbracelet/bubbletea"
	"github.com/charmbracelet/lipgloss"
	"github.com/godbus/dbus/v5"
)

type model struct {
	networks      []map[string]interface{}
	selected      int
	loading       bool
	spinner       spinner.Model
	viewport      viewport.Model
	passwordInput textinput.Model
	showPassword  bool
	statusMsg     string
	width         int
	height        int
}

var (
	titleStyle = lipgloss.NewStyle().
			Foreground(lipgloss.Color("#FFFFFF")).
			Background(lipgloss.Color("#FF5F87")).
			Padding(0, 1)

	listStyle = lipgloss.NewStyle().
			Border(lipgloss.RoundedBorder()).
			BorderForeground(lipgloss.Color("#874BFD")).
			Padding(1, 2)

	selectedStyle = lipgloss.NewStyle().
			Foreground(lipgloss.Color("#FF5F87"))

	statusStyle = lipgloss.NewStyle().
			Foreground(lipgloss.Color("#A550DF")).
			Bold(true)
)

func initialModel() model {
	s := spinner.New()
	s.Spinner = spinner.Dot
	s.Style = lipgloss.NewStyle().Foreground(lipgloss.Color("205"))

	ti := textinput.New()
	ti.Placeholder = "Enter password..."
	ti.EchoMode = textinput.EchoPassword
	ti.Focus()

	return model{
		loading:       true,
		spinner:       s,
		passwordInput: ti,
		viewport:      viewport.New(0, 0),
	}
}

func (m model) Init() tea.Cmd {
	return tea.Batch(
		m.spinner.Tick,
		scanNetworks,
		resizeWait,
	)
}

func (m model) Update(msg tea.Msg) (tea.Model, tea.Cmd) {
	switch msg := msg.(type) {
	case tea.KeyMsg:
		if m.showPassword {
			switch msg.Type {
			case tea.KeyEnter:
				return m, connectToNetwork(m.networks[m.selected], m.passwordInput.Value())
			case tea.KeyEsc:
				m.showPassword = false
				return m, nil
			}
		}

		switch msg.String() {
		case "q", "ctrl+c":
			return m, tea.Quit
		case "up", "k":
			if m.selected > 0 {
				m.selected--
			}
		case "down", "j":
			if m.selected < len(m.networks)-1 {
				m.selected++
			}
		case "enter":
			if len(m.networks) > 0 {
				m.showPassword = true
				m.passwordInput.Focus()
			}
		case "r":
			m.loading = true
			return m, scanNetworks
		}
	case tea.WindowSizeMsg:
		m.width = msg.Width
		m.height = msg.Height
		m.viewport.Width = msg.Width - 4
		m.viewport.Height = msg.Height - 8
	case []map[string]interface{}:
		m.networks = msg
		m.loading = false
	case statusMessage:
		m.statusMsg = string(msg)
		return m, tea.ClearScreen
	case error:
		m.statusMsg = fmt.Sprintf("Error: %v", msg)
		return m, nil
	}

	var cmd tea.Cmd
	if m.loading {
		m.spinner, cmd = m.spinner.Update(msg)
	} else if m.showPassword {
		m.passwordInput, cmd = m.passwordInput.Update(msg)
	}
	return m, cmd
}

func (m model) View() string {
	if m.width == 0 || m.height == 0 {
		return ""
	}

	var b strings.Builder

	b.WriteString(titleStyle.Render("WiFi Manager") + "\n\n")

	if m.loading {
		b.WriteString(fmt.Sprintf("%s Scanning networks...\n\n", m.spinner.View()))
		return lipgloss.Place(
			m.width, m.height,
			lipgloss.Center, lipgloss.Center,
			b.String(),
		)
	}

	if m.showPassword {
		content := fmt.Sprintf(
			"Connecting to: %s\n\n%s\n\n%s",
			m.networks[m.selected]["ssid"],
			m.passwordInput.View(),
			"(esc to cancel)",
		)
		return lipgloss.Place(
			m.width, m.height,
			lipgloss.Center, lipgloss.Center,
			listStyle.Render(content),
		)
	}

	var listContent strings.Builder
	for i, network := range m.networks {
		ssid := network["ssid"].(string)
		strength := network["strength"].(uint8)
		security := network["security"].(string)

		item := fmt.Sprintf("%s %s [%d%%] %s",
			if i == m.selected {
				item = selectedStyle.Render("> " + item)
			} else {
				item = "  " + item
			}

		listContent.WriteString(item + "\n")
	}

	m.viewport.SetContent(listStyle.Render(listContent.String()))
	b.WriteString(m.viewport.View())

	if m.statusMsg != "" {
		b.WriteString("\n" + statusStyle.Render(m.statusMsg))
	}

	return b.String()
}

func main() {
	p := tea.NewProgram(initialModel(), tea.WithAltScreen())
	if _, err := p.Run(); err != nil {
		fmt.Printf("Error: %v", err)
		os.Exit(1)
	}
}

// NetworkManager integration
func scanNetworks() tea.Msg {
	conn, err := dbus.SystemBus()
	if err != nil {
		return err
	}
	defer conn.Close()

	obj := conn.Object("org.freedesktop.NetworkManager", "/org/freedesktop/NetworkManager")
	var devices []dbus.ObjectPath
	if err := obj.Call("org.freedesktop.DBus.Properties.Get", 0,
		"org.freedesktop.NetworkManager", "Devices").Store(&devices); err != nil {
		return err
	}

	var wifiDevice dbus.ObjectPath
	for _, dev := range devices {
		devObj := conn.Object("org.freedesktop.NetworkManager", dev)
		var devType uint32
		if err := devObj.Call("org.freedesktop.DBus.Properties.Get", 0,
			"org.freedesktop.NetworkManager.Device", "DeviceType").Store(&devType); err != nil {
			continue
		}
		if devType == 2 { // WiFi device
			wifiDevice = dev
			break
		}
	}

	// Request scan
	wifiObj := conn.Object("org.freedesktop.NetworkManager", wifiDevice)
	if err := wifiObj.Call("org.freedesktop.NetworkManager.Device.Wireless.RequestScan", 0,
		map[string]interface{}{}).Err; err != nil {
		return err
	}

	time.Sleep(2 * time.Second) // Wait for scan to complete

	var aps []dbus.ObjectPath
	if err := wifiObj.Call("org.freedesktop.DBus.Properties.Get", 0,
		"org.freedesktop.NetworkManager.Device.Wireless", "AccessPoints").Store(&aps); err != nil {
		return err
	}

	var networks []map[string]interface{}
	for _, ap := range aps {
		apObj := conn.Object("org.freedesktop.NetworkManager", ap)
		props := make(map[string]dbus.Variant)
		if err := apObj.Call("org.freedesktop.DBus.Properties.GetAll", 0,
			"org.freedesktop.NetworkManager.AccessPoint").Store(&props); err != nil {
			continue
		}

		strength := props["Strength"].Value().(byte)
		ssid := string(props["Ssid"].Value().([]byte))
		flags := props["Flags"].Value().(uint32)
		wpaFlags := props["WpaFlags"].Value().(uint32)
		rsnFlags := props["RsnFlags"].Value().(uint32)

		security := "Open"
		if (flags|wpaFlags|rsnFlags)&0x1 != 0 {
			security = "WEP"
		}
		if (wpaFlags|rsnFlags)&0x300 != 0 {
			security = "WPA"
		}
		if (wpaFlags|rsnFlags)&0xac != 0 {
			security = "WPA2"
		}
		if (wpaFlags|rsnFlags)&0x400 != 0 {
			security = "WPA3"
		}

		networks = append(networks, map[string]interface{}{
			"ssid":     ssid,
			"strength": strength,
			"security": security,
			"path":     ap,
		})
	}

	return networks
}

func connectToNetwork(network map[string]interface{}, password string) tea.Cmd {
	return func() tea.Msg {
		conn, err := dbus.SystemBus()
		if err != nil {
			return err
		}
		defer conn.Close()

		settings := make(map[string]map[string]interface{})
		settings["802-11-wireless"] = map[string]interface{}{
			"ssid": []byte(network["ssid"].(string)),
		}
		if password != "" {
			settings["802-11-wireless-security"] = map[string]interface{}{
				"key-mgmt": "wpa-psk",
				"psk":      password,
			}
		}

		obj := conn.Object("org.freedesktop.NetworkManager", "/org/freedesktop/NetworkManager/Settings")
		var path dbus.ObjectPath
		if err := obj.Call("org.freedesktop.NetworkManager.Settings.AddConnection", 0,
			settings).Store(&path); err != nil {
			return err
		}

		deviceObj := conn.Object("org.freedesktop.NetworkManager", "/org/freedesktop/NetworkManager")
		if _, err := deviceObj.Call("org.freedesktop.NetworkManager.ActivateConnection", 0,
			path, dbus.ObjectPath("/"), network["path"]); err != nil {
			return err
		}

		return statusMessage("Connected successfully!")
	}
}

type statusMessage string

func resizeWait() tea.Msg {
	time.Sleep(time.Millisecond * 100)
	return tea.WindowSizeMsg{} // Trigger resize
}

