#!/usr/bin/env python3

from pydbus import SystemBus
from gi.repository import GLib

loop = GLib.MainLoop()
bus = SystemBus()

NM = bus.get("org.freedesktop.NetworkManager")

def get_status():
    state = NM.State  # 70 = connected global, 50 = disconnected

    if state == 70 and NM.PrimaryConnection:
        # Obtenir l'objet ActiveConnection depuis le chemin
        primary_path = NM.PrimaryConnection
        primary = bus.get(".NetworkManager", primary_path)

        conn_type = primary.Type
        if conn_type == "802-3-ethernet":
            print("network-wired-symbolic")
        elif conn_type == "802-11-wireless":
            # Obtenir le device via le chemin spÃ©cifique
            device_path = primary.Devices[0] if primary.Devices else None
            if device_path:
                device = bus.get(".NetworkManager", device_path)
                strength = getattr(device, "Strength", 0)

                if strength == 0:
                    print("network-wireless-signal-none-symbolic")
                elif strength < 30:
                    print("network-wireless-signal-weak-symbolic")
                elif strength < 60:
                    print("network-wireless-signal-ok-symbolic")
                elif strength < 80:
                    print("network-wireless-signal-good-symbolic")
                else:
                    print("network-wireless-signal-excellent-symbolic")
            else:
                print("network-wireless-offline-symbolic")
        else:
            print("network-wireless-offline-symbolic")
    else:
        if not NM.WirelessEnabled:
            print("network-wireless-disabled-symbolic")
        else:
            print("network-wireless-offline-symbolic")

# Connexion au signal de changement d'Ã©tat
def on_state_change(*args):
    get_status()

NM.onStateChanged = on_state_change

# Premier appel au dÃ©marrage
get_status()

loop.run()

